function EmailConfigSave(){var a="api.config.php",e=$("#email_host").val(),o=$("#email_username").val(),t=$("#email_password").val(),n=$("#email_port").val(),l=$("#email_address").val(),i=$("#email_name").val();$.ajax({url:a,cache:!1,dataType:"json",type:"POST",data:{calling:"Config",action:"UpdateEmail",email_host:e,email_username:o,email_password:t,email_port:n,email_address:l,email_name:i},error:function(){console.log("Request Error")}}).done(function(a){console.log("Return: "+a.message),$("#email-save-state").fadeIn().delay(1e3).fadeOut()}).error()}function FacebookConfigSave(){var a="api.config.php",e=$("#facebook_app_id").val(),o=$("#facebook_app_secret").val();$.ajax({url:a,cache:!1,dataType:"json",type:"POST",data:{calling:"Config",action:"UpdateFacebook",facebook_app_id:e,facebook_app_secret:o},error:function(){console.log("Request Error")}}).done(function(a){console.log("Return: "+a.message),$("#facebook-sdk-save-state").fadeIn().delay(1e3).fadeOut()}).error()}function MetaConfigSave(){var a="api.config.php",e=$("#meta_title").val(),o=$("#meta_description").val(),t=$("#meta_sitename").val(),n=$("#meta_author").val(),l=$("#meta_keyword").val();$.ajax({url:a,cache:!1,dataType:"json",type:"POST",data:{calling:"Config",action:"UpdateMeta",meta_title:e,meta_description:o,meta_sitename:t,meta_author:n,meta_keyword:l},error:function(){console.log("Request Error")}}).done(function(a){$("#meta-save-state").fadeIn().delay(1e3).fadeOut(),console.log("Return: "+a.message)}).error()}