function FacebookShare(t){return window.open("https://www.facebook.com/sharer/sharer.php?u="+t,"Facebook Sharing","_blank"),!1}function TwitterShare(t,e){return window.open("https://twitter.com/intent/tweet?url="+e+"&text="+t,"Twitter Tweet","_blank"),!1}function PinterestShare(t,e,n){return window.open("//pinterest.com/pin/create/link/?url="+e+"&media="+n+"&description="+t,"Pinterest","_blank"),!1}$(document).ready(function(){var t=$("#microdata-link").val(),e=$("#microdata-title").val(),n=$("#microdata-image").val();$("#facebook-sharing-btn").click(function(){FacebookShare(t)}),$("#twitter-sharing-btn").click(function(){TwitterShare(e,t)}),$("#pinterest-sharing-btn").click(function(){PinterestShare(e,t,n)})});