function SetCover(o,e){var r="api.product.php";$.ajax({url:r,cache:!1,dataType:"json",type:"POST",data:{calling:"Product",action:"SetCover",product_id:o,image_id:e},error:function(){console.log("Request Error")}}).done(function(){location.reload()}).error()}function RemovePhoto(o,e){var r="api.product.php";$.ajax({url:r,cache:!1,dataType:"json",type:"POST",data:{calling:"Product",action:"DeletePhoto",product_id:o,image_id:e},error:function(){console.log("Request Error")}}).done(function(){$("#image-"+e).hide(500)}).error()}