function SetAdmin(n,e){var r="api.user.php";if($("#control-btn-"+n).html('<i class="fa fa-spinner fa-spin"></i>'),""==n||""==e)return!1;if("administrator"==e)action="UnsetAdmin";else{if("member"!=e)return!1;action="SetAdmin"}$.ajax({url:r,cache:!1,dataType:"json",type:"POST",data:{calling:"User",action:action,member_id:n},error:function(){console.log("Request Error")}}).done(function(n){console.log("Return: "+n.message),location.reload()}).error()}